/*!/wp-content/plugins/accordion-and-accordion-slider/assets/js/wp-aas-public.js*/jQuery(document).ready(function($){$('.wpos-tab-slider').each(function(index){var slider_id=$(this).attr('id');var slider_conf=$.parseJSON($(this).closest('.wpaas-accordion-wrap').find('.wpaas-conf').text());if(typeof(slider_id)!='undefined'&&slider_id!=''){jQuery('#'+slider_id).wpostabSlider({width:parseInt(slider_conf.width),height:parseInt(slider_conf.height),responsiveMode:'custom',orientation:slider_conf.orientation,openPanelOn:slider_conf.open_panel_on,visiblePanels:parseInt(slider_conf.visible_panels),panelDistance:parseInt(slider_conf.panel_distance),maxOpenedPanelSize:slider_conf.max_openedaccordion_size,touchSwipe:!1,mouseWheel:!1,openPanelDuration:400,closePanelDuration:400,pageScrollDuration:300,mouseWheel:(slider_conf.mouse_wheel)=="true"?!0:!1,autoplay:(slider_conf.autoplay)=="true"?!0:!1,shadow:(slider_conf.shadow)=="true"?!0:!1,breakpoints:{960:{visiblePanels:(parseInt(slider_conf.visible_panels)>5)?5:parseInt(slider_conf.visible_panels)},800:{visiblePanels:(parseInt(slider_conf.visible_panels)>3)?3:parseInt(slider_conf.visible_panels)},650:{visiblePanels:3},500:{visiblePanels:3}}})}});$(document).on("click","#more-slide-right-1",function(){$('#wpaas-accordion-1').wpostabSlider('openPanel',5);$("#more-slide-left-1").css("display","block")});$(document).on("click","#more-slide-left-1",function(){$('#wpaas-accordion-1').wpostabSlider('openPanel',0);$("#more-slide-right-1").css("display","block")});$(document).on("click","#more-slide-right-2",function(){$('#wpaas-accordion-2').wpostabSlider('openPanel',5);$("#more-slide-left-2").css("display","block")});$(document).on("click","#more-slide-left-2",function(){$('#wpaas-accordion-2').wpostabSlider('openPanel',0);$("#more-slide-right-2").css("display","block")})});